fun checker(related: Array<String>): Int {
    val size = related.size

    val matrix = Array(size) { IntArray(size) }

    related.forEachIndexed { index, pairs ->
        pairs.split("").forEachIndexed { relationIndex, relation ->
            matrix[index][relationIndex] = relation.toInt()
        }
    }

    val graph = Array(size) { mutableListOf<Int>() }
    for (i in 0 until size) {
        for (j in 0 until size) {
            if (matrix[i][j] == 1) { // There's a relationship (i sends gift to j or vice versa)
                graph[i].add(j)
                graph[j].add(i)
            }
        }
    }

    val visited = BooleanArray(size)
    var groupCount = 0

    for (person in 0 until size) {
        if (!visited[person]) {
            // If person hasn't been visited, it's a new group
            dfs(person, graph, visited)
            groupCount++
        }
    }

    return groupCount
}
